'use strict';

var React = require('react-native');
var MHRemoterBase = require('./MHRemoterBase').MHRemoterBase;
var IRV2_DEVICE_TYPE = require('../ConstDefine/IRV2PublicDefine').IRV2_DEVICE_TYPE;
var NewAirBoxRemote = require('../../MainV2/AllRemoter/NewAirBoxRemote');
var {
  Component,
} = React;


/**
 * 空调
 **/
class MHRemoterAirBox extends MHRemoterBase{
  constructor(props) {
    super(props);

    this.props.IconName = "/DeviceList/device_list_aircon.png";
    this.props.componentInfo = NewAirBoxRemote;
    this.props.DeviceType = IRV2_DEVICE_TYPE.DEVICE_TYPE_AIR_CONDITIONER;
    this.props.DeviceTitle = "空调";
  }

}

module.exports.MHRemoterAirBox = MHRemoterAirBox;
