'use strict';

var React = require('react-native');
var MHRemoterBase = require('./MHRemoterBase').MHRemoterBase;
var IRV2_DEVICE_TYPE = require('../ConstDefine/IRV2PublicDefine').IRV2_DEVICE_TYPE;
var AirPurifierBox = require('../../MainV2/AllRemoter/AirPurifierBoxRemote');

var {
  Component,
} = React;


/**
 * 净化器
 **/
class MHRemoterAirPurifier extends MHRemoterBase{
  constructor(props) {
    super(props);

    this.props.IconName = "/DeviceList/device_list_airpurifier.png";
    this.props.componentInfo = AirPurifierBox;
    this.props.DeviceType = IRV2_DEVICE_TYPE.DEVICE_TYPE_PURIFIER;
    this.props.DeviceTitle = "空气净化器";
  }

}

module.exports.MHRemoterAirPurifier = MHRemoterAirPurifier;
