'use strict';

var React = require('react-native');
var MHRemoterBase = require('./MHRemoterBase').MHRemoterBase;
var IRV2_DEVICE_TYPE = require('../ConstDefine/IRV2PublicDefine').IRV2_DEVICE_TYPE;
var TopBox = require('../../MainV2/AllRemoter/TopBoxRemote');

var {
  Component,
} = React;


/**
 * 小米遥控器
 **/
class MHRemoterBox extends MHRemoterBase{
  constructor(props) {
    super(props);

    this.props.IconName = "/DeviceList/device_list_box.png";
    this.props.componentInfo = TopBox;
    this.props.OtherKeyList = ["up"];
    this.props.DeviceType = IRV2_DEVICE_TYPE.DEVICE_TYPE_BOX;
    this.props.DeviceTitle = "盒子";
  }

}

module.exports.MHRemoterBox = MHRemoterBox;
