'use strict';

var React = require('react-native');
var MHRemoterBase = require('./MHRemoterBase').MHRemoterBase;
var IRV2_DEVICE_TYPE = require('../ConstDefine/IRV2PublicDefine').IRV2_DEVICE_TYPE;
var CameraBox = require('../../MainV2/AllRemoter/CameraBoxRemote');

var {
  Component,
} = React;


/**
 * 照相机
 **/
class MHRemoterCamera extends MHRemoterBase{
  constructor(props) {
    super(props);

    this.props.IconName = "/DeviceList/device_list_camera.png";
    this.props.componentInfo = CameraBox;
    this.props.DeviceType = IRV2_DEVICE_TYPE.DEVICE_TYPE_CAMERA;
    this.props.DeviceTitle = "照相机";
  }

}

module.exports.MHRemoterCamera = MHRemoterCamera;
