'use strict';

var React = require('react-native');
var MHRemoterBase = require('./MHRemoterBase').MHRemoterBase;
var IRV2_DEVICE_TYPE = require('../ConstDefine/IRV2PublicDefine').IRV2_DEVICE_TYPE;

var {
  Component,
} = React;


/**
 * DVD
 **/
class MHRemoterDVD extends MHRemoterBase{
  constructor(props) {
    super(props);

    this.props.IconName = "/DeviceList/device_list_dvd.png";
    this.props.componentInfo = "DvdInfo";
    this.props.DeviceType = IRV2_DEVICE_TYPE.DEVICE_TYPE_DVD;
    this.props.DeviceTitle = "DVD";
  }

}

module.exports.MHRemoterDVD = MHRemoterDVD;
