'use strict';

var React = require('react-native');
var MHRemoterBase = require('./MHRemoterBase').MHRemoterBase;
var IRV2_DEVICE_TYPE = require('../ConstDefine/IRV2PublicDefine').IRV2_DEVICE_TYPE;
var ElectricFanBox = require('../../MainV2/AllRemoter/ElectricFanBoxRemote');
var {
  Component,
} = React;


/**
 * 风扇遥控器
 **/
class MHRemoterFan extends MHRemoterBase{
  constructor(props) {
    super(props);

    this.props.IconName = "/DeviceList/device_list_fan.png";
    this.props.componentInfo = ElectricFanBox;
    this.props.DeviceType = IRV2_DEVICE_TYPE.DEVICE_TYPE_FAN;
    this.props.DeviceTitle = "风扇";
  }

}

module.exports.MHRemoterFan = MHRemoterFan;
