'use strict';

var React = require('react-native');
var MHRemoterBase = require('./MHRemoterBase').MHRemoterBase;
var IRV2_DEVICE_TYPE = require('../ConstDefine/IRV2PublicDefine').IRV2_DEVICE_TYPE;
var HeaterBox = require('../../MainV2/AllRemoter/HeaterBoxRemoter');

var {
  Component,
} = React;


/**
 * 小米遥控器
 **/
class MHRemoterHeater extends MHRemoterBase{
  constructor(props) {
    super(props);

    this.props.IconName = "/DeviceList/device_list_light.png";
    this.props.componentInfo = HeaterBox;
    this.props.DeviceType = IRV2_DEVICE_TYPE.DEVICE_TYPE_HEATER;
    this.props.DeviceTitle = "热水器";
  }

}

module.exports.MHRemoterHeater = MHRemoterHeater;
