'use strict';

var React = require('react-native');
var MHRemoterBase = require('./MHRemoterBase').MHRemoterBase;
var IRV2_DEVICE_TYPE = require('../ConstDefine/IRV2PublicDefine').IRV2_DEVICE_TYPE;
var LightBox = require('../../MainV2/AllRemoter/LightBoxRemote');

var {
  Component,
} = React;


/**
 * 风扇遥控器
 **/
class MHRemoterLight extends MHRemoterBase{
  constructor(props) {
    super(props);

    this.props.IconName = "/DeviceList/device_list_light.png";
    this.props.componentInfo = LightBox;
    this.props.DeviceType = IRV2_DEVICE_TYPE.DEVICE_TYPE_IPTV_LIGHT;
    this.props.DeviceTitle = "灯";
  }

}

module.exports.MHRemoterLight = MHRemoterLight;
