'use strict';

var React = require('react-native');
var MHRemoterBase = require('./MHRemoterBase').MHRemoterBase;
var IRV2_DEVICE_TYPE = require('../ConstDefine/IRV2PublicDefine').IRV2_DEVICE_TYPE;

var {
  Component,
} = React;


/**
 * 投影仪
 **/
class MHRemoterProjector extends MHRemoterBase{
  constructor(props) {
    super(props);

    this.props.IconName = "/DeviceList/device_list_projector.png";
    this.props.componentInfo = "ProjectorInfo";
    this.props.DeviceType = IRV2_DEVICE_TYPE.DEVICE_TYPE_PROJECTOR;
    this.props.DeviceTitle = "投影仪";
  }

}

module.exports.MHRemoterProjector = MHRemoterProjector;
