'use strict';

var React = require('react-native');
var MHRemoterBase = require('./MHRemoterBase').MHRemoterBase;
var IRV2_DEVICE_TYPE = require('../ConstDefine/IRV2PublicDefine').IRV2_DEVICE_TYPE;
var SoundBox = require('../../MainV2/AllRemoter/SoundBoxRemote');
var {
  Component,
} = React;


/**
 * 小米遥控器
 **/
class MHRemoterSound extends MHRemoterBase{
  constructor(props) {
    super(props);

    this.props.IconName = "/DeviceList/device_list_amplifier.png";
    this.props.componentInfo = SoundBox;
    this.props.DeviceType = IRV2_DEVICE_TYPE.DEVICE_TYPE_PA;
    this.props.DeviceTitle = "功放";
  }

}

module.exports.MHRemoterSound = MHRemoterSound;
