'use strict';

var React = require('react-native');
var MHRemoterBase = require('./MHRemoterBase').MHRemoterBase;
var IRV2_DEVICE_TYPE = require('../ConstDefine/IRV2PublicDefine').IRV2_DEVICE_TYPE;
var TVBox = require('../../MainV2/AllRemoter/TVBoxRemote');
var {
  Component,
} = React;


/**
 * 小米遥控器
 **/
class MHRemoterTV extends MHRemoterBase{
  constructor(props) {
    super(props);
    this.props.IconName = "/DeviceList/device_list_tv.png";
    this.props.componentInfo = TVBox;
    this.props.DeviceType = IRV2_DEVICE_TYPE.DEVICE_TYPE_TV;
    this.props.DeviceTitle = "电视";
  }

}

module.exports.MHRemoterTV = MHRemoterTV;
