'use strict';

var React = require('react-native');
var MHRemoterBase = require('./MHRemoterBase').MHRemoterBase;
var IRV2_DEVICE_TYPE = require('../ConstDefine/IRV2PublicDefine').IRV2_DEVICE_TYPE;
var TopBox = require('../../MainV2/AllRemoter/TopBoxRemote');
var {
  Component,
} = React;

/**
 * 机顶盒
 **/
class MHRemoterTopBox extends MHRemoterBase{
  constructor(props) {
    super(props);

    this.props.IconName = "/DeviceList/device_list_STB.png";
    this.props.componentInfo = TopBox;
    this.props.DeviceType = IRV2_DEVICE_TYPE.DEVICE_TYPE_TOPBOX;
    this.props.DeviceTitle = "机顶盒";
  }

}

module.exports.MHRemoterTopBox = MHRemoterTopBox;
