class ButtonModelFactory extends React.Component{
  
  static GetButtonImage_WithName(name){
    
      var model = imageModel[name];
      if(model ==undefined){
            model= imageModel['DEFAULT'];
      }
      return model;
  }

  static Air_GetButtonImage_WithName(name){
    
      var model = airImageModel[name];
      if(model ==undefined){
         model= airImageModel['DEFAULT'];
      }
      return model;
  }



}

module.exports.ButtonModelFactory = ButtonModelFactory;