'use strict';

var React = require('react-native');

var {
  Component,
} = React;

/**
 *
 **/
class IRV2Utils extends React.Component{

    constructor(props, context) {
      super(props, context);
      
    }

    /**
     * 产生一个随机的ID
     * charactorSet：限定的字符集
     * max：ID长度
     * idSet：已有的ID集合，去重检查用
     **/
    static genRandomID(charactorSet, max, idSet) {

      console.log('charactorSet:'+charactorSet);
      console.log('max:'+max);
      console.log('idSet:'+idSet);

      var result;
      while (true) {
          result = "";
          for (var i = 0; i < max; ++i) {
              var indexRandom = Math.random()* charactorSet.length;
              result += charactorSet.charAt(indexRandom);
          }
          if (idSet.indexOf(result) == -1) {
              idSet.push(result);
              return result;
          }
      }

    }

    /**
     * 返回当前时间: 从1970.1.1到现在的时间跨度秒数
     **/
    static curTimeSeconds() {
      var now = new Date();
      var nowSeconds = Math.round(now.getTime() / 1000);
      return nowSeconds;
    }

}

module.exports.IRV2Utils = IRV2Utils;
