'use strict'
var React = require('react-native');
import { NativeModules } from 'react-native';
var MHPluginSDK = NativeModules.MHPluginSDK;
var IRV2Utils = require('../../MainV2/MHRemoterListManager/IRV2Utils').IRV2Utils;

var {
  Component,
} = React;
var keyChars = "0123456789";

class StudyModel extends React.Component{

    constructor(props, context) {
      super(props, context);
    }

    /* 获取学习ID */
    static getStudyKey() {
      var studyKeyList = new Array();
      return IRV2Utils.genRandomID(keyChars, 15, studyKeyList);
    }
}

module.exports.StudyModel = StudyModel;
