'use strict';
var React = require('react-native');

let instance = null;
var name = '';

export default class AppStatusManager {
    constructor(props, context) {
      super(props, context);             
      if (!instance) {
        instance = this;
      }
      return instance;
  }

  /***
  * 类方法
  */
  static ShareInstance(){
    let singleton = new AppStatusManager();
    return singleton;
  }

  /***
  * 实例方法
  */
  setName(name){
    this.name = name;
  }

  getName(){
    return this.name;
  }
}

module.exports.AppStatusManager = AppStatusManager;