import { NativeModules } from 'react-native';
var MHPluginSDK = NativeModules.MHPluginSDK;
const APPBAR_HEIGHT = Platform.OS === 'ios' ? 44 : 56;
const NavigatorBarHeight = (screenHeight==812)?(88):( APPBAR_HEIGHT +20);

 class NormalExtendView extends React.Component {
  constructor(props, context) {
    super(props, context);

    this.state = {
      did: MHPluginSDK.deviceId,
      model: MHPluginSDK.deviceModel,
      apiLevel: MHPluginSDK.apiLevel,
      basePath: MHPluginSDK.basePath,
      requestStatus: false,
      clicked: 'none',
      devMode: MHPluginSDK.devMode,
      cloumnCount: 3,
      totalCount: this.props.totalCount,
    };
  }

  render() {

    return (
      <View style={styles.containerAll} >
  
        {this.renderAllItem()}

      </View>
    );
  }

  renderAllItem() {
  
    var allItemDate = [];
    for(var i =0 ;i<dataSource.length;i++){

      let title = dataSource[i];
      allItemDate.push(

        <View style ={styles.columnItemBgView} >
            <TitleButton style={styles.buttonView}  key ={title+i} onPress ={()=> this.onPressButton(title)} key = {title} title ={title}  titleSize={15} textColor="rgb(11,11,1)" >
            </TitleButton>
        </View>

      );

    }
    return allItemDate;

  }

  onPressButton (event){
    alert(event);

  }

  componentWillMount() {
    
  }

  componentWillUnmount() {

  }
}


